stickMenu();

jQuery(window).scroll(function () {
    stickMenu();
});

function stickMenu() {

    var menu = $('#menu');
    var menuPosition = $(window).scrollTop() - $('#dayline_logo').height();
    if (menuPosition < 0) {
        menu.css('position', 'relative').css('top', '');
    } else {
        menu.css('position', 'fixed').css('top', '0px');
    }
}

$(document).ready(function () {

    var logo = $('#dayline_logo');
    var height = logo.height();
    logo.css('top', '-' + height + 'px').css('position', 'relative');

    $("#cssmenu").prepend('<div id="menu-button">Menu</div>');
    $("#cssmenu #menu-button").on("click", function () {
        var e = $(this).next("ul");
        if (e.hasClass("open")) {
            e.removeClass("open")
        } else {
            e.addClass("open")
        }
    })

    logo.animate({
        top: '0'
    })
})
